update SqlQuery set SqlQueryText='SELECT KOD_PRODUKTU ''ProductTypeId'', PERIODA_FIXACE ''FixedRatePeriod'', NULLIF(MANDANT, 0) ''MandantId'', NOVY_PRODUKT ''IsNewProduct'', MAX(ALGORITMUS_SAZBY) ''InterestRateAlgorithm'', CAST(CASE WHEN CAST(GETDATE() as date) BETWEEN MAX(ISNULL([PLATNOST_OD], ''1901-01-01'')) AND MAX(ISNULL([PLATNOST_DO], ''9999-12-31'')) THEN 1 ELSE 0 END as bit) ''IsValid'' FROM SBR.HTEDM_CIS_PERIODY_FIXACE_V GROUP BY KOD_PRODUKTU, NOVY_PRODUKT, NULLIF(MANDANT, 0), PERIODA_FIXACE' where SqlQueryId='FixedRatePeriods'